import firebase from "firebase/app";
import "firebase/app";
import "firebase/firestore"; //firebaseのデータベース
import "firebase/auth"; //firebaseの認証機能

const firebaseApp = firebase.initializeApp({
    apiKey: process.env.RECT_APP_FIREBASE_APIKEY,
    authDomain: process.env.RECT_APP_FIREBASE_DOMAIN,
    databaseURL:process.env.RECT_APP_FIREBASE_DATABASE,
    projectId: process.env.RECT_APP_FIREBASE_PROJECT_ID,
    storageBucket: process.env.RECT_APP_FIREBASE_STORAGE_BUCKET,
    messagingSenderId: process.env.RECT_APP_FIREBASE_SENDER_ID,
    appId: process.env.RECT_APP_FIREBASE_APP_ID,
});

export const db = firebaseApp.firestore();
export const auth = firebase.auth();
